const { type } = msg.payload
const events = flow.get('events')
const prev_light_state = events[type].light

const input_event = msg.payload.light
const falsy = !input_event
const is_not_an_object = typeof input_event !== 'object'
const is_array = Array.isArray(input_event)
const is_a_function = typeof input_event === 'function'

const error_message = 'light property must be an object'

if (falsy || is_not_an_object || is_array || is_a_function) 
    throw new Error(error_message)

const light_state = {
    ...prev_light_state, ...input_event
}

msg.payload = {
    ...events[type],
    ...msg.payload,
    light: light_state
}

return msg

